import React, { useEffect, useState } from 'react'
import { fetchData } from '../services/api'

export default function Home() {
    let [products, setProducts] = useState([]);
    useEffect(() => {
        async function getProducts() {
            let data = await fetchData();
            setProducts(data.products);
        }
        getProducts();
    }, [])

    function RatingStars({ rating }) {
        const filledStars = Math.floor(rating);
        const emptyStars = 5 - filledStars;

        return (
            <div>
                {/* CAROUSEL SECTION */}
                <div id="carouselExampleControls" class="carousel slide" data-bs-ride="carousel">
                    <div class="carousel-inner">
                        <div class="carousel-item active">
                            <img src="/Images/sale.jpg" class="d-block w-100" alt="..." loading="lazy" />
                        </div>
                        <div class="carousel-item">
                            <img src="/Images/sale2.jpeg" class="d-block w-100" alt="..." />
                        </div>
                        <div class="carousel-item">
                            <img src="/Images/sale3.jpeg" class="d-block w-100" alt="..." />
                        </div>
                    </div>
                    <button class="carousel-control-prev" type="button" data-bs-target="#carouselExampleControls" data-bs-slide="prev">
                        <span class="carousel-control-prev-icon" aria-hidden="true"></span>
                        <span class="visually-hidden">Previous</span>
                    </button>
                    <button class="carousel-control-next" type="button" data-bs-target="#carouselExampleControls" data-bs-slide="next">
                        <span class="carousel-control-next-icon" aria-hidden="true"></span>
                        <span class="visually-hidden">Next</span>
                    </button>
                </div>

                {/* PRODUCT SECTION */}
                <div className='product display mt-5'>
                    <div className='container-fluid'>
                        <div className='row'>
                            {products.map(product => (
                                <div key={product.id} className="col-12 col-md-6 col-lg-4 mb-4">
                                    <div class="card">
                                        <img src={product.thumbnail} class="card-img-top" alt="..." />
                                        <div class="card-body">
                                            <h5 class={product.title}>Card title</h5>
                                            <h6>Rating : ({product.rating}/5)</h6>
                                        </div>
                                    </div>
                                </div>
                            ))}
                        </div>
                    </div>
                </div>

            </div>
        )
    }}
