import React, { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import { useCart } from "../context/CartContext";
import { useSearch } from "../context/SearchContext";
import { useFilter } from "../context/FilterContext";
import FilterPopup from "./FilterPopup";

export default function Home() {
  const [products, setProducts] = useState([]);
  const [loading, setLoading] = useState(true);
  const [visibleCount, setVisibleCount] = useState(36);
  const [showFilter, setShowFilter] = useState(false);

  const navigate = useNavigate();
  const { addToCart } = useCart();
  const { searchTerm } = useSearch();
  const { selectedCategory, priceRange } = useFilter();

  // Fetch products
  useEffect(() => {
    fetch("https://dummyjson.com/products?limit=194")
      .then(res => res.json())
      .then(data => {
        setProducts(data.products);
        setLoading(false);
      })
      .catch(err => {
        console.error("Error fetching products:", err);
        setLoading(false);
      });
  }, []);

  // Reset visible count when search/filter changes
  useEffect(() => {
    setVisibleCount(36);
  }, [searchTerm, selectedCategory, priceRange]);

  const handleAddToCart = (product) => {
    addToCart(
      {
        id: product.id,
        title: product.title,
        price: product.price * 90,
        image: product.thumbnail,
        brand: product.brand
      },
      1
    );
    alert(`${product.title} added to cart!`);
  };

  const handleBuyNow = (product) => {
    handleAddToCart(product);
    navigate("/cart");
  };

  const handleProductClick = (id) => {
    navigate(`/product/${id}`);
  };

  // ðŸ” FILTER LOGIC
  const filteredProducts = products.filter(product => {
    const matchesSearch =
      product.title.toLowerCase().includes(searchTerm.toLowerCase()) ||
      product.brand?.toLowerCase().includes(searchTerm.toLowerCase()) ||
      product.category?.toLowerCase().includes(searchTerm.toLowerCase());

    const matchesCategory =
      selectedCategory === "all" ||
      product.category === selectedCategory;

    const matchesPrice =
      product.price * 90 <= priceRange[1];

    return matchesSearch && matchesCategory && matchesPrice;
  });

  const visibleProducts = filteredProducts.slice(0, visibleCount);

  return (
    <div>
      {/* CAROUSEL */}
      <div
        id="carouselExampleControls"
        className="carousel slide"
        data-bs-ride="carousel"
      >
        <div className="carousel-inner">
          <div className="carousel-item active">
            <img src="/Images/sale.jpg" className="d-block w-100" alt="Sale 1" />
          </div>
          <div className="carousel-item">
            <img src="/Images/sale2.jpeg" className="d-block w-100" alt="Sale 2" />
          </div>
          <div className="carousel-item">
            <img src="/Images/sale3.jpeg" className="d-block w-100" alt="Sale 3" />
          </div>
        </div>
      </div>

      {/* FILTER BAR */}
      <div className="container-fluid mt-4">
        <div className="d-flex justify-content-between align-items-center">
          <h5 className="mb-0">Products</h5>
          <button
            className="btn btn-outline-dark d-flex align-items-center gap-2"
            onClick={() => setShowFilter(true)}
          >
            <i className="bi bi-funnel"></i>
            Filter
          </button>
        </div>
      </div>

      {showFilter && <FilterPopup onClose={() => setShowFilter(false)} />}

      {/* PRODUCTS */}
      <div className="container-fluid mt-4">
        {loading ? (
          <div className="text-center py-5">
            <div className="spinner-border text-dark"></div>
          </div>
        ) : (
          <>
            <div className="row">
              {visibleProducts.map(product => (
                <div
                  key={product.id}
                  className="col-12 col-md-6 col-lg-4 mb-4"
                >
                  <div className="card h-100 shadow-sm">
                    <div
                      style={{ cursor: "pointer" }}
                      onClick={() => handleProductClick(product.id)}
                    >
                      <img
                        src={product.thumbnail}
                        className="card-img-top"
                        alt={product.title}
                        style={{ height: "250px", objectFit: "cover" }}
                      />
                    </div>

                    <div className="card-body d-flex flex-column">
                      <h6
                        style={{ cursor: "pointer" }}
                        onClick={() => handleProductClick(product.id)}
                      >
                        {product.title}
                      </h6>

                      <p className="text-muted small">
                        {product.brand || product.category}
                      </p>

                      <h5 className="fw-bold">
                        â‚¹{(product.price * 90).toFixed(2)}
                      </h5>

                      <div className="mt-auto d-grid gap-2">
                        <button
                          className="btn btn-primary"
                          onClick={() => handleBuyNow(product)}
                        >
                          Buy Now
                        </button>
                        <button
                          className="btn btn-outline-dark"
                          onClick={(e) => {
                            e.stopPropagation();
                            handleAddToCart(product);
                          }}
                        >
                          Add to Cart
                        </button>
                      </div>
                    </div>
                  </div>
                </div>
              ))}
            </div>

            {/* VIEW MORE */}
            {visibleCount < filteredProducts.length && (
              <div className="text-center my-4">
                <button
                  className="btn btn-outline-dark px-4 py-2"
                  onClick={() => setVisibleCount(prev => prev + 36)}
                >
                  View More
                </button>
              </div>
            )}

            {/* NO RESULTS */}
            {filteredProducts.length === 0 && (
              <div className="text-center text-muted mt-5">
                No products found.
              </div>
            )}
          </>
        )}
      </div>
    </div>
  );
}
